import sys
import bioinfo

"""
  Placez vous methodes de tests ci-dessous.

  Pour vos tests, vous pouvez vous aider des exemples suivants:

  Une courte sequence d'ADN valide
"""
short_adn  = "gatcctccatatacaacggtatctccacctcaggtttagatctcaacaacggaaccattgccgacatgagac"
long_adn = ""
invalid_adn = long_adn + "z" + short_adn

palindrome1 = "ttaggatt";
palindrome2 = "catggcactttgagcattttagccgattttacgagtttcacggtac"
""""
  Une longue sequence d'ADN valide


  Un petit palindrome

  Un autre palindrome

  Une chaine qui n'est pas une chaine ADN
"""


def test(did_pass):
    """  Imprime le rÃ©sultat d'un teste.  """
    linenum = sys._getframe(1).f_lineno
    if did_pass:
        msg = "Test at line {0} ok.".format(linenum)
    else:
        msg = ("Test at line {0} FAILED.".format(linenum))
    print(msg)

def test_is_adn():
    # Example d'un test; ajoutez des autres tests
    test(bioinfo.is_adn(short_adn)==True)
    test(bioinfo.is_adn(long_adn)==True)
    test(bioinfo.is_adn(invalid_adn)==False)
    test(bioinfo.is_adn('atx')==False)
    test(bioinfo.is_adn('atg')==True )


def test_positions():
    test(bioinfo.positions("ACGACCG","cg")==[1,5])
    test(bioinfo.positions("ACGACCG","x")==[])
    test(bioinfo.positions("ACGACCG","gac")==[2])

def test_distance_h():
    test(bioinfo.distance_h("ATGAC","TGACG")==5)
    test(bioinfo.distance_h("A","A")==0)
    test(bioinfo.distance_h("AG","GG")==1)
    test(bioinfo.distance_h("AG","AT")==1)
    test(bioinfo.distance_h("ATGAC","ATGAC")==0)
    test(bioinfo.distance_h("ATGAC","AGGAG")==2)
    test(bioinfo.distance_h("ABCD","ABC")==None)

def test_plus_long_palindrome():
    test(bioinfo.plus_long_palindrome("ACCTGTTAGGATTC")=="TTAGGATT")
    test(bioinfo.plus_long_palindrome(palindrome1)==palindrome1)
    test(bioinfo.plus_long_palindrome(palindrome2)==palindrome2)

test_is_adn()
test_positions()
test_distance_h()
test_plus_long_palindrome()
